import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { useParams, Link } from "react-router-dom";
import { connect } from "react-redux";

function SingleCocktail() {
  const [dataJoin, setDataJoin] = useState([]);
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    setDataJoin(innerJoinResponse(data.users, data.album, data.photo));
  }, [data.album]);

  const innerJoinResponse = (users, album, photo) => {
    // let newPeople = people.filter((person) => person.id !== id);
    // setPeople(newPeople);
    let newAlbum = [];
    // return { ...users, album };
    album.map((alb) => {
      alb.users = users.filter((user) => user.id === alb.userId);
      alb.photo = photo.filter((phot) => phot.albumId === alb.id);

      newAlbum.push(alb);
    });

    setTimeout(() => {
      setLoading(false);
    }, 500);
    return newAlbum;
  };

  return <></>;
}

const mapStateToProps = (state) => ({
  data: state.data,
});

SingleCocktail = connect(mapStateToProps, {})(SingleCocktail);

export default SingleCocktail;
