import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { fetchData } from "../action/fetchData.js";
import CocktailList from "../components/CocktailList";
import SearchForm from "../components/SearchForm";

function Home(props) {
  const { fetchData, data } = props;
  const [dataJoin, setDataJoin] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    setDataJoin(innerJoinResponse(data.users, data.album, data.photo));
  }, [data.album]);

  const innerJoinResponse = (users, album, photo) => {
    // let newPeople = people.filter((person) => person.id !== id);
    // setPeople(newPeople);
    let newAlbum = [];
    // return { ...users, album };
    album.map((alb) => {
      alb.users = users.filter((user) => user.id === alb.userId);
      alb.photo = photo.filter((phot) => phot.albumId === alb.id);

      newAlbum.push(alb);
    });
    return newAlbum;
  };

  console.log("isinya ni", dataJoin);
  return (
    <main>
      <SearchForm />
      {/* <CocktailList /> */}
    </main>
  );
}

const mapStateToProps = (state) => ({
  data: state.data,
});

Home = connect(mapStateToProps, {
  fetchData,
})(Home);

export default Home;
