import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { useParams, Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchData } from "../action/fetchData.js";
import Cocktail from "../components/Cocktail";

function SingleCocktail(props) {
  const { data, fetchData } = props;
  const { id } = useParams();
  const [dataJoin, setDataJoin] = useState([]);
  const [loading, setLoading] = useState(false);

  console.log("id", id);

  useEffect(() => {
    fetchData();
    setLoading(true);
  }, []);

  useEffect(() => {
    setDataJoin(innerJoinResponse(data.users, data.album, data.photo));
  }, [data.album]);

  const innerJoinResponse = (users, album, photo) => {
    // let newPeople = people.filter((person) => person.id !== id);
    // setPeople(newPeople);
    let newAlbum = [];
    // return { ...users, album };
    album.map((alb) => {
      alb.users = users.filter((user) => user.id === alb.userId);
      alb.photo = photo.filter((phot) => phot.albumId === alb.id);

      newAlbum.push(alb);
    });

    setTimeout(() => {
      setLoading(false);
    }, 1000);
    return newAlbum;
  };
  console.log("masuk cuy", dataJoin);

  if (loading) {
    return <Loading />;
  }

  return <></>;
}

const mapStateToProps = (state) => ({
  data: state.data,
});

SingleCocktail = connect(mapStateToProps, {
  fetchData,
})(SingleCocktail);

export default SingleCocktail;
