import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { useParams, Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchData } from "../action/fetchData.js";
import Thumbnail from "../components/Thumbnail";

function SingleCocktail(props) {
  const { data, fetchData } = props;
  const { id } = useParams();
  const [dataJoin, setDataJoin] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchData();
    setLoading(true);
  }, []);

  useEffect(() => {
    setDataJoin(innerJoinResponse(data.photo, id));
  }, [data.album]);

  const innerJoinResponse = (photo, id) => {
    console.log("fotonya", photo);
    let newAlbum = photo.filter((ph) => ph.albumId === id);

    setTimeout(() => {
      setLoading(false);
    }, 1000);
    return newAlbum;
  };
  console.log("masuk cuy", dataJoin);

  if (loading) {
    return <Loading />;
  }

  return (
    <section className="section">
      <h2 className="section-title">cocktails</h2>
      <div className="cocktails-center">
        {dataJoin.map((item) => {
          return <Thumbnail key={item.id} {...item} />;
        })}
      </div>
    </section>
  );
}

const mapStateToProps = (state) => ({
  data: state.data,
});

SingleCocktail = connect(mapStateToProps, {
  fetchData,
})(SingleCocktail);

export default SingleCocktail;
