import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { useParams, Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchData } from "../action/fetchData.js";
import Thumbnail from "../components/Thumbnail";

function SingleCocktail(props) {
  const { data } = props;
  const { id } = useParams();
  const [dataJoin, setDataJoin] = useState([]);
  const [loading, setLoading] = useState(false);

  console.log("datanya", data);

  if (loading) {
    return <Loading />;
  }

  return (
    <section className="section">
      <h2 className="section-title">cocktails</h2>
      <div className="cocktails-center">
        {data.selectedThumbnail.map((item) => {
          return <Thumbnail key={item.id} {...item} />;
        })}
      </div>
    </section>
  );
}

const mapStateToProps = (state) => ({
  data: state.data,
});

SingleCocktail = connect(mapStateToProps, {
  fetchData,
})(SingleCocktail);

export default SingleCocktail;
