import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { useParams, Link } from "react-router-dom";
import { connect } from "react-redux";
import { fetchData } from "../action/fetchData.js";
import Thumbnail from "../components/Thumbnail";

function SingleCocktail(props) {
  const { data, fetchData } = props;
  const { id } = useParams();
  const [dataJoin, setDataJoin] = useState([]);
  const [loading, setLoading] = useState(false);

  // useEffect(() => {
  //   fetchData();
  //   setLoading(true);
  // }, []);

  // useEffect(() => {
  //   setDataJoin(innerJoinResponse(data.users, data.album, data.photo));
  // }, [data.album]);

  const innerJoinResponse = (users, album, photo) => {
    // let newPeople = people.filter((person) => person.id !== id);
    // setPeople(newPeople);
    let newAlbum = [];
    // return { ...users, album };
    album.map((alb) => {
      alb.users = users.filter((user) => user.id === alb.userId);
      alb.photo = photo.filter((phot) => phot.albumId === alb.id);

      newAlbum.push(alb);
    });

    setTimeout(() => {
      setLoading(false);
    }, 500);

    newAlbum = newAlbum.filter((albums) => albums.id === id);
    console.log("hasilnya ", newAlbum);
    return newAlbum;
  };

  if (loading) {
    return <Loading />;
  }

  return (
    <section className="section">
      <h2 className="section-title">cocktails</h2>
      <div className="cocktails-center">
        {data.selectedThumbnail.map((item) => {
          return <Thumbnail key={item.id} {...item} />;
        })}
      </div>
    </section>
  );
}

const mapStateToProps = (state) => ({
  data: state.data,
});

SingleCocktail = connect(mapStateToProps, {
  fetchData,
})(SingleCocktail);

export default SingleCocktail;
